mapper.select:
select *
from Approve
where quit_pernr = #{pernr}
  and launch_id = #{launchId}
order by isnull(approve_time, GETDATE()), approve_content

select top 1 area_pernr
from Approve
where quit_pernr = #{quitPernr}

mapper.update:
update Approve
approve_time = #{approveTime}, approve_result = #{approveResult}, approve_result_desc = #{approveResultDesc}, reviewer_pernr = #{reviewerPernr}, approve_opinion = #{approveOpinion}, work = #{work}, fixed_asset = #{fixedAsset}, isa = #{isa}, dormitory = #{dormitory}, goods_refund = #{goodsRefund}, document_num = #{documentNum}, region_pernr = #{regionPernr}, area_pernr = #{areaPernr}, loan_money = #{loanMoney}, short_money = #{shortMoney}, short_store_id = #{shortStoreId}, quality_money = #{qualityMoney}, care_money = #{careMoney}, care_document_num = #{careDocumentNum} card_money = #{cardMoney}, clothes_money = #{clothesMoney},
where launch_id = #{launchId} and quit_pernr = #{quitPernr} and approve_content = #{approveContent}

mapper.delete:
delete
from Approve
where quit_pernr = #{quitPernr}
  and approve_content = #{approveContent}

delete
from Approve
where quit_pernr = #{quitPernr}
  and launch_id = #{launchId}

mapper.select:
select max(send_time)
from Approve
where quit_pernr = #{pernr}
  and approve_content = #{approveContent}

select approve_result
from Approve
where quit_pernr = #{quitPernr}
  and launch_id = #{launchId}

select top 1 *
from Approve
where quit_pernr = #{quitPernr}
  and reviewer_pernr = #{reviewerPernr}
order by send_time desc
