mapper.delete:
delete
from ImportData
where quit_pernr = #{quitPernr}
  and convert(date, import_time) = #{importTime}

mapper.select:
select *
from ImportData
where quit_pernr = #{quitPernr}
  and convert(date, import_time) = #{importTime}

select *
from ImportData
where convert(date, import_time) = #{importTime}
